mapper.select:
SELECT
    (
        SELECT
            count(*)
        FROM
            we_customer where state=#{newGroup.emplCodeState}
    ) as addCustomerNumber,
    (
        SELECT
            count(*)
        FROM
            we_customer where date_format(add_time,'%Y-%m-%d') = date_format(curdate(),'%Y-%m-%d') and state=#{newGroup.emplCodeState}
    ) as tdAddCustomerNumber,
    (
        SELECT
            count(*)
        FROM we_group_member where state=#{newGroup.groupCodeState}
    ) as joinGroupCustomerNumber,
    (
        SELECT
            count(*)
        FROM we_group_member WHERE date_format(join_time,'%Y-%m-%d') = date_format(curdate(),'%Y-%m-%d') and state=#{newGroup.groupCodeState}
    ) as tdJoinGroupCustomerNumber

SELECT wc.avatar, wc.customer_name, wc.customer_type, wc.external_userid AS externalUserid, wc.add_user_id, wc.add_time, wc.gender, (SELECT GROUP_CONCAT(DISTINCT su.user_name) from sys_user su where su.we_user_id=wc.add_user_id) as addUserName, (SELECT count(*) from we_group_member wgm WHERE wc.external_userid = wgm.user_id and wgm.state=#{query.groupState} and wgm.quit_scene IS NULL) as joinGroupNumber, IF((SELECT count(*) from we_group_member wgm WHERE wc.external_userid = wgm.user_id and wgm.state=#{query.groupState} and wgm.quit_scene IS NULL)>0,1,0) as isJoinGroup FROM we_customer wc
and wc.state = #{query.state}
and wc.customer_name LIKE CONCAT('%',#{query.customerName,jdbcType=VARCHAR},'%')
AND date_format(wcr.add_time,'%Y-%m-%d') BETWEEN #{query.startAddTime} AND #{query.endAddTime}
HAVING(joinGroupNumber)>0
