mapper.select:
SELECT wcr.id FROM we_customer wcr


        AND wcr.is_join_blacklist=#{weCustomerList.isJoinBlacklist}


        AND wcr.gender=#{weCustomerList.gender}


        AND wcr.gender in

            #{item}



        AND wcr.track_state=#{weCustomerList.trackState}


       AND wcr.track_state not in

           #{item}




        AND wcr.add_method=#{weCustomerList.addMethod}



        AND wcr.customer_type=#{weCustomerList.customerType}



           AND wcr.customer_type in

               #{item}




        AND wcr.external_userid=#{weCustomerList.externalUserid}



       AND wcr.external_userid in

           #{item}




        AND wcr.add_user_id=#{weCustomerList.firstUserId}



        AND wcr.del_flag = #{weCustomerList.delFlag}


       AND wcr.phone = #{weCustomerList.phone,jdbcType=VARCHAR}


        AND  CONCAT( IFNULL(wcr.customer_name,''))
        LIKE CONCAT('%',#{weCustomerList.name,jdbcType=VARCHAR},'%')


        AND wcr.add_user_id in

            #{item}






                OR FIND_IN_SET(#{weCustomerList.tagIds},wcr.tag_ids)



                    AND !FIND_IN_SET(#{tagId},wcr.tag_ids)





                    AND FIND_IN_SET(#{tagId},wcr.tag_ids)







        AND ISNULL(wcr.tag_ids) or LENGTH(TRIM(wcr.tag_ids)) =0



            AND !FIND_IN_SET(#{excludeTagId},wcr.tag_ids)



        AND  date_format(wcr.add_time,'%Y-%m-%d') BETWEEN #{weCustomerList.beginTime} AND #{weCustomerList.endTime}



        GROUP BY  wcr.external_userid


ORDER BY wcr.add_time desc


        limit ${pageDomain.pageSize * (pageDomain.pageNum-1)}, #{pageDomain.pageSize}

SELECT wcr.id FROM we_customer wcr
LEFT JOIN sys_user su on su.we_user_id=wcr.add_user_id


        AND  CONCAT( IFNULL(wcr.customer_name,''),IFNULL(su.user_name,''))
        LIKE CONCAT('%',#{weCustomerList.name,jdbcType=VARCHAR},'%')


        AND wcr.customer_type=#{weCustomerList.customerType}



        AND wcr.track_state=#{weCustomerList.trackState}



        AND wcr.add_method=#{weCustomerList.addMethod}





            AND FIND_IN_SET(#{tagId},wcr.tag_ids)




        AND  date_format(wcr.add_time,'%Y-%m-%d') BETWEEN #{weCustomerList.beginTime} AND #{weCustomerList.endTime}



        AND wcr.add_user_id=#{weCustomerList.firstUserId}



        AND wcr.del_flag = #{weCustomerList.delFlag}



ORDER BY wcr.add_time desc


        limit ${pageDomain.pageSize * (pageDomain.pageNum-1)}, #{pageDomain.pageSize}

SELECT count(wcr.id) FROM we_customer wcr
LEFT JOIN sys_user su on su.we_user_id=wcr.add_user_id


        AND  CONCAT( IFNULL(wcr.customer_name,''),IFNULL(su.user_name,''))
        LIKE CONCAT('%',#{weCustomerList.name,jdbcType=VARCHAR},'%')



        AND wcr.customer_type=#{weCustomerList.customerType}



        AND wcr.track_state=#{weCustomerList.trackState}



        AND wcr.add_method=#{weCustomerList.addMethod}





            AND FIND_IN_SET(#{tagId},wcr.tag_ids)




        AND  date_format(wcr.add_time,'%Y-%m-%d') BETWEEN #{weCustomerList.beginTime} AND #{weCustomerList.endTime}



        AND wcr.add_user_id=#{weCustomerList.firstUserId}



        AND wcr.del_flag = #{weCustomerList.delFlag}

SELECT wc.id, wc.unionid, wc.avatar, wc.customer_name, wc.customer_type as customerType, wc.external_userid, wc.add_user_id as firstUserId, wc.track_state, wc.add_method, wc.add_time as firstAddTime, wc.birthday, wc.gender, wc.phone, wc.email, wc.address, wc.qq, wc.is_join_blacklist as isJoinBlacklist, wc.position, wc.corp_name as corpName, wc.other_descr as otherDescr, timestampdiff(day,wc.add_time, wc.loss_time) as retentionDays, (SELECT GROUP_CONCAT(DISTINCT wu.user_name) FROM sys_user wu WHERE wu.we_user_id=wc.add_user_id) as userName, (SELECT GROUP_CONCAT(wt.tag_id) FROM we_flower_customer_tag_rel wfctr LEFT JOIN we_tag wt ON wt.tag_id= wfctr.tag_id WHERE wfctr.external_userid=wc.external_userid AND wc.add_user_id=wfctr.user_id AND wt.tag_type=3 and wfctr.del_flag=0) as personTagIds, (SELECT GROUP_CONCAT(wt.name) FROM we_flower_customer_tag_rel wfctr LEFT JOIN we_tag wt ON wt.tag_id= wfctr.tag_id WHERE wfctr.external_userid=wc.external_userid AND wc.add_user_id=wfctr.user_id AND wt.tag_type=3 and wfctr.del_flag=0) as personTagNames, (SELECT GROUP_CONCAT(wt.name) FROM we_flower_customer_tag_rel wfctr LEFT JOIN we_tag wt ON wt.tag_id= wfctr.tag_id WHERE wfctr.external_userid=wc.external_userid AND wc.add_user_id=wfctr.user_id AND wt.tag_type=1 and wfctr.del_flag=0) as tagNames, (SELECT GROUP_CONCAT(wt.tag_id) FROM we_flower_customer_tag_rel wfctr LEFT JOIN we_tag wt ON wt.tag_id= wfctr.tag_id WHERE wfctr.external_userid=wc.external_userid AND wc.add_user_id=wfctr.user_id AND wt.tag_type=1 and wfctr.del_flag=0) as tagIds, wc.update_time as updateTime, wc.area, wc.loss_time FROM we_customer wc WHERE wc.id in
#{id}
ORDER BY wc.add_time desc

