mapper.insert:
INSERT INTO we_customer( id, external_userid, customer_name, avatar, customer_type, gender, unionid, birthday, corp_name, `position`, create_time, create_by, add_user_id, add_time, qq, email, address, phone, add_method, del_flag, `state`, update_by, update_time, other_descr, remark_name, create_by_id, update_by_id, tag_ids ) values
(#{item.id},#{item.externalUserid},#{item.customerName},#{item.avatar},#{item.customerType},#{item.gender}, #{item.unionid},#{item.birthday}, #{item.corpName},#{item.position}, #{item.createTime}, #{item.createBy},#{item.addUserId},#{item.addTime},#{item.qq},#{item.email},#{item.address},#{item.phone},#{item.addMethod},#{item.delFlag}, #{item.state},#{item.updateBy},#{item.updateTime},#{item.otherDescr},#{item.remarkName},#{item.createById},#{item.updateById}, #{item.tagIds} )
ON DUPLICATE KEY UPDATE customer_name=IFNULL(VALUES(customer_name),we_customer.customer_name), avatar=IFNULL(VALUES(avatar), we_customer.avatar), customer_type=IFNULL(VALUES(customer_type),we_customer.customer_type), gender=IFNULL(VALUES(gender), we_customer.gender), unionid=IFNULL(VALUES(unionid), we_customer.unionid), birthday=IFNULL(VALUES(birthday), we_customer.birthday), corp_name=IFNULL(VALUES(corp_name), we_customer.corp_name), `position`=IFNULL(VALUES(`position`), we_customer.position), add_user_id=IFNULL(VALUES(add_user_id), we_customer.add_user_id), add_time=IFNULL(VALUES(add_time),we_customer.add_time), qq=IFNULL(VALUES(qq), we_customer.qq), email=IFNULL(VALUES(email), we_customer.email), address=IFNULL(VALUES(address), we_customer.address), phone=IFNULL(VALUES(phone), we_customer.phone), add_method=IFNULL(VALUES(add_method), we_customer.add_method), del_flag=IFNULL(VALUES(del_flag),we_customer.del_flag), `state`=IFNULL(VALUES(`state`),we_customer.state), create_by=IFNULL(VALUES(create_by),we_customer.create_by), create_by_id=IFNULL(VALUES(create_by_id),we_customer.create_by_id), update_by=IFNULL(VALUES(update_by),we_customer.update_by), update_by_id=IFNULL(VALUES(update_by_id),we_customer.update_by_id), update_time=IFNULL(VALUES(update_time),we_customer.update_time), other_descr=IFNULL(VALUES(other_descr),we_customer.other_descr), tag_ids=IFNULL(VALUES(tag_ids),we_customer.tag_ids), remark_name=IFNULL(VALUES(remark_name),we_customer.remark_name);

mapper.select:
select distinct wc.external_userid, wc.add_user_id from we_customer wc left join sys_user su on wc.add_user_id = su.we_user_id and su.del_flag = 0 left join sys_user_dept sud on sud.we_user_id = su.we_user_id and sud.del_flag = 0 left join we_flower_customer_tag_rel wfctr on wfctr.external_userid = wc.external_userid and wfctr.del_flag = 0
and su.we_user_id in
#{item}
and sud.dept_id in
#{item}
AND FIND_IN_SET(#{tagId},wfctr.tag_id)
and date_format(wc.add_time,'%Y-%m-%d') >= #{beginTime}
and date_format(wc.add_time,'%Y-%m-%d') <= #{endTime}
and wc.state in
#{state}
and wc.del_flag = 0

SELECT wcr.external_userid FROM we_customer wcr
LEFT JOIN sys_user su on su.we_user_id=wcr.add_user_id


        AND  CONCAT( IFNULL(wcr.customer_name,''),IFNULL(su.user_name,''))
        LIKE CONCAT('%',#{weCustomerList.name,jdbcType=VARCHAR},'%')


        AND wcr.customer_type=#{weCustomerList.customerType}



        AND wcr.track_state=#{weCustomerList.trackState}


        AND wcr.track_state != 5


        AND wcr.add_method=#{weCustomerList.addMethod}



            AND FIND_IN_SET(#{tagId},wcr.tag_ids)




        AND  date_format(wcr.add_time,'%Y-%m-%d') BETWEEN #{weCustomerList.beginTime} AND #{weCustomerList.endTime}



        AND wcr.add_user_id in

            #{item}





        AND wcr.del_flag = #{weCustomerList.delFlag}


ORDER BY wcr.add_time desc

select sdd.date, count(wc.id) as customer_number, count(case when wc.del_flag = 0 then 1 end) as efficient_number from ( SELECT `date` FROM sys_dim_date
and `date` >= date_format(#{startTime},'%y-%m-%d')
and `date` <= date_format(#{endTime},'%y-%m-%d')
) as sdd left join we_customer wc on date_format(wc.add_time, '%Y-%m-%d')= date_format(sdd.date, '%Y-%m-%d')
AND wc.state=#{state}
group by sdd.date asc

select date_format(add_time,'%Y-%m-%d') as `date`, count(1) as customer_number, count(case when del_flag = 0 then 1 end) as efficient_number from we_customer where id in (select id from we_customer where state=#{state}
and date_format(add_time,'%y-%m-%d') >= date_format(#{startTime},'%y-%m-%d')
and date_format(add_time,'%y-%m-%d') <= date_format(#{endTime},'%y-%m-%d')
) group by add_time

select  external_userid,customer_name,avatar from we_customer

    and external_userid in

        #{item}


group by external_userid,customer_name,avatar;
