mapper.insert:
insert into we_group_member (chat_id, user_id, union_id, join_time, join_scene, `type`, group_nick_name,`name`,invitor_user_id, create_by, create_time,update_by, update_time,del_flag,create_by_id, update_by_id,state) values
(#{weGroupMember.chatId},#{weGroupMember.userId},#{weGroupMember.unionId}, #{weGroupMember.joinTime},#{weGroupMember.joinScene},#{weGroupMember.type}, #{weGroupMember.groupNickName},#{weGroupMember.name},#{weGroupMember.invitorUserId}, #{weGroupMember.createBy},#{weGroupMember.createTime}, #{weGroupMember.updateBy},#{weGroupMember.updateTime},#{weGroupMember.delFlag},#{weGroupMember.createById},#{weGroupMember.updateById}, #{weGroupMember.state})
on duplicate key update join_time= values(join_time),join_scene= values(join_scene), group_nick_name= values(group_nick_name),`name`= values(`name`),invitor_user_id= values(invitor_user_id), update_time= values(update_time), update_by= values(update_by), update_by_id= values(update_by_id), state= values(state),del_flag= values(del_flag), `type`= values(`type`);

mapper.select:
SELECT distinct wgm.chat_id, wgm.join_time, wgm.type, wgm.join_scene, wgm.name, (case when su.avatar is null then wc.avatar else su.avatar end) as avatar FROM we_group_member wgm left join sys_user su on su.we_user_id = wgm.user_id and wgm.type = 1 left join we_customer wc on wc.external_userid = wgm.user_id and wgm.type = 2
and wgm.chat_id in
#{chatId}

select date_format(join_time,'%Y-%m-%d') as `date`, count(distinct user_id) as member_number, count(distinct case when del_flag = 0 then user_id end) as efficient_number from we_group_member where id in (select id from we_group_member where state=#{state}
and date_format(join_time,'%y-%m-%d') >= date_format(#{startTime},'%y-%m-%d')
and date_format(join_time,'%y-%m-%d') <= date_format(#{endTime},'%y-%m-%d')
) group by join_time
