mapper.select:
SELECT sdd.date, ( SELECT count(DISTINCT wgm.user_id) FROM we_group_member wgm WHERE DATE_FORMAT( wgm.join_time, '%Y-%m-%d' )= DATE_FORMAT( sdd.date, '%Y-%m-%d' )
AND wgm.state in
#{state}
) AS tdJoinGroupNmber, ( SELECT count(DISTINCT vc.union_id) FROM we_keyword_group_view_count vc WHERE DATE_FORMAT( vc.create_time, '%Y-%m-%d' )= DATE_FORMAT( sdd.date, '%Y-%m-%d' )
AND vc.keyword_group_id=#{keyWordGroupId}
) AS tdViewNumber FROM sys_dim_date sdd WHERE DATE_FORMAT( sdd.date, '%Y-%m-%d' ) BETWEEN #{beginTime} AND #{endTime}
