mapper.select:
select wks.id, wks.name, wks.type, wks.kf_id, wks.open_kf_id, wks.scenes, wks.url, wks.qr_code, wks.create_time, wks.create_by, wks.create_by_id, wks.update_by, wks.update_by_id, wks.update_time, wki.name as kf_name, wki.avatar as kf_avatar, aa.access_cnt, aa.consult_cnt, aa.reception_cnt from we_kf_scenes wks left join we_kf_info wki on wks.open_kf_id = wki.open_kf_id and wki.del_flag = 0 left join ( select count(distinct case when wkem.event_type = 'enter_session' then concat(wkem.open_kf_id,wkem.external_userid) end) as access_cnt, count(distinct case when wkm.origin = 3 then concat(wkm.open_kf_id,wkm.external_userid) end) as consult_cnt, count(distinct case when wkm.servicer_userid is not null then concat(wkm.open_kf_id,wkm.external_userid) end) as reception_cnt, wkem.scene from we_kf_event_msg wkem left join we_kf_msg wkm on wkem.open_kf_id = wkm.open_kf_id and wkem.external_userid = wkm.external_userid group by wkem.scene ) as aa on aa.scene = wks.scenes
and wks.id in
#{id}
and wks.name like concat('%',#{name},'%')
and wks.type = #{type}
and wks.open_kf_id = #{openKfId}
AND date_format(wks.create_time,'%y%m%d') >= date_format(#{beginTime},'%y%m%d')
AND date_format(wks.create_time,'%y%m%d') <= date_format(#{endTime},'%y%m%d')
and wks.del_flag = 0
