mapper.select:
SELECT x_time, @visit_total := @visit_total + visit_cnt AS visit_total_cnt, @consult_total := @consult_total + consult_cnt AS consult_total_cnt, @reception_total := @reception_total + reception_cnt AS reception_total_cnt, visit_cnt as td_visit_cnt, consult_cnt as td_consult_cnt, reception_cnt as td_reception_cnt FROM ( SELECT date_list.`date` AS x_time, IFNULL(real_data.visit_num, 0) AS visit_cnt, IFNULL(real_data.consult_num, 0) AS consult_cnt, IFNULL(real_data.reception_num, 0) AS reception_cnt FROM ( select `date` from sys_dim_date where `date` >= DATE_FORMAT(#{beginTime},'%Y-%m-%d') and `date` <= DATE_FORMAT(#{endTime},'%Y-%m-%d') ) AS date_list LEFT JOIN ( SELECT DATE_FORMAT(wkp.create_time, '%Y-%m-%d') DAY, count(1) AS visit_num, sum(CASE WHEN wkp.session_start_time is not null THEN 1 ELSE 0 END) AS consult_num, sum(CASE WHEN wkp.user_id is not null THEN 1 ELSE 0 END) AS reception_num FROM we_kf_pool wkp where 1=1
and wkp.scene in
#{scene}
and wkp.open_kf_id in
#{openKfId}
and wkp.user_id in
#{userId}
and DATE_FORMAT(wkp.create_time,'%Y-%m-%d') >= DATE_FORMAT(#{beginTime},'%Y-%m-%d') and DATE_FORMAT(wkp.create_time,'%Y-%m-%d') <= DATE_FORMAT(#{endTime},'%Y-%m-%d') GROUP BY DAY, ( SELECT @visit_total := (SELECT count(1) FROM we_kf_pool wkp
and wkp.scene in
#{scene}
and wkp.open_kf_id in
#{openKfId}
and wkp.user_id in
#{userId}
and DATE_FORMAT(wkp.create_time, '%Y-%m-%d') < DATE_FORMAT(#{beginTime}, '%Y-%m-%d'))
), (SELECT @consult_total := (SELECT count(1) FROM we_kf_pool wkp where wkp.session_start_time is not null
and wkp.scene in
#{scene}
and wkp.open_kf_id in
#{openKfId}
and wkp.user_id in
#{userId}
and DATE_FORMAT(wkp.create_time, '%Y-%m-%d') < DATE_FORMAT(#{beginTime}, '%Y-%m-%d'))), (SELECT @reception_total := (SELECT count(1) FROM we_kf_pool wkp where wkp.user_id is not null
and wkp.scene in
#{scene}
and wkp.open_kf_id in
#{openKfId}
and wkp.user_id in
#{userId}
and DATE_FORMAT(wkp.create_time, '%Y-%m-%d') < DATE_FORMAT(#{beginTime}, '%Y-%m-%d')))) AS real_data ON date_list.`date` = real_data.day) AS RESULT ORDER BY x_time asc

SELECT x_time, @visit_total := @visit_total - visit_cnt AS visit_total_cnt, @consult_total := @consult_total - consult_cnt AS consult_total_cnt, @reception_total := @reception_total - reception_cnt AS reception_total_cnt, visit_cnt as td_visit_cnt, consult_cnt as td_consult_cnt, reception_cnt as td_reception_cnt FROM ( SELECT date_list.`date` AS x_time, IFNULL(real_data.visit_num, 0) AS visit_cnt, IFNULL(real_data.consult_num, 0) AS consult_cnt, IFNULL(real_data.reception_num, 0) AS reception_cnt FROM ( select `date` from sys_dim_date where `date` >= DATE_FORMAT(#{beginTime},'%Y-%m-%d') and `date` <= DATE_FORMAT(#{endTime},'%Y-%m-%d') ) AS date_list LEFT JOIN ( SELECT DATE_FORMAT(wkp.create_time, '%Y-%m-%d') DAY, count(1) AS visit_num, sum(CASE WHEN wkp.session_start_time is not null THEN 1 ELSE 0 END) AS consult_num, sum(CASE WHEN wkp.user_id is not null THEN 1 ELSE 0 END) AS reception_num FROM we_kf_pool wkp where 1=1
and wkp.scene in
#{scene}
and wkp.open_kf_id in
#{openKfId}
and wkp.user_id in
#{userId}
and DATE_FORMAT(wkp.create_time,'%Y-%m-%d') >= DATE_FORMAT(#{beginTime},'%Y-%m-%d') and DATE_FORMAT(wkp.create_time,'%Y-%m-%d') <= DATE_FORMAT(#{endTime},'%Y-%m-%d') GROUP BY DAY, ( SELECT @visit_total := (SELECT count(1) FROM we_kf_pool wkp
and wkp.scene in
#{scene}
and wkp.open_kf_id in
#{openKfId}
and wkp.user_id in
#{userId}
and DATE_FORMAT(wkp.create_time, '%Y-%m-%d') <= DATE_FORMAT(#{endTime}, '%Y-%m-%d'))
), (SELECT @consult_total := (SELECT count(1) FROM we_kf_pool wkp where wkp.session_start_time is not null
and wkp.scene in
#{scene}
and wkp.open_kf_id in
#{openKfId}
and wkp.user_id in
#{userId}
and DATE_FORMAT(wkp.create_time, '%Y-%m-%d') <= DATE_FORMAT(#{endTime}, '%Y-%m-%d'))), (SELECT @reception_total := (SELECT count(1) FROM we_kf_pool wkp where wkp.user_id is not null
and wkp.scene in
#{scene}
and wkp.open_kf_id in
#{openKfId}
and wkp.user_id in
#{userId}
and DATE_FORMAT(wkp.create_time, '%Y-%m-%d') <= DATE_FORMAT(#{endTime}, '%Y-%m-%d')))) AS real_data ON date_list.`date` = real_data.day) AS RESULT ORDER BY x_time desc

SELECT x_time, @reply_total := ifnull(@reply_total,0) + reply_cnt AS reply_total_cnt, reply_cnt FROM (SELECT date_list.`date` AS x_time, IFNULL(real_data.reply_num, 0) AS reply_cnt FROM ( select `date` from sys_dim_date where `date` >= DATE_FORMAT(#{beginTime},'%Y-%m-%d') and `date` <= DATE_FORMAT(#{endTime},'%Y-%m-%d') ) AS date_list LEFT JOIN (select date_format(aa.create_time, '%Y-%m-%d') DAY, sum(case when end_time is null then 0 else 1 end) reply_num from (select wkm.create_time, min(case when wkm.origin = 5 and wkm.send_time >= wkp.session_start_time and wkm.send_time <= wkp.session_end_time then wkm.send_time end) as end_time from we_kf_pool wkp left join we_kf_msg wkm on wkp.corp_id = wkm.corp_id and wkp.open_kf_id = wkm.open_kf_id and wkp.external_userid = wkm.external_userid where 1=1
and wkp.open_kf_id in
#{openKfId}
and wkp.user_id in
#{userId}
and DATE_FORMAT(wkp.create_time,'%Y-%m-%d') >= DATE_FORMAT(#{beginTime},'%Y-%m-%d') and DATE_FORMAT(wkp.create_time,'%Y-%m-%d') <= DATE_FORMAT(#{endTime},'%Y-%m-%d') group by wkp.id, wkp.open_kf_id, wkp.external_userid) as aa group by DAY, (SELECT @reply_total := (select sum(case when end_time is null then 0 else 1 end) reply_num from (select wkm.create_time, min(case when wkm.origin = 5 and wkm.send_time >= wkp.session_start_time and wkm.send_time <= wkp.session_end_time then wkm.send_time end) as end_time from we_kf_pool wkp left join we_kf_msg wkm on wkp.corp_id = wkm.corp_id and wkp.open_kf_id = wkm.open_kf_id and wkp.external_userid = wkm.external_userid where 1=1
and wkp.open_kf_id in
#{openKfId}
and wkp.user_id in
#{userId}
and DATE_FORMAT(wkp.create_time, '%Y-%m-%d') < DATE_FORMAT(#{beginTime}, '%Y-%m-%d') group by wkp.id, wkp.open_kf_id, wkp.external_userid) as aa))) AS real_data ON date_list.`date` = real_data.day ORDER BY DAY ASC) AS RESULT ORDER BY x_time asc

SELECT x_time, @reply_total := ifnull(@reply_total,0) - reply_cnt AS reply_total_cnt, reply_cnt FROM (SELECT date_list.`date` AS x_time, IFNULL(real_data.reply_num, 0) AS reply_cnt FROM ( select `date` from sys_dim_date where `date` >= DATE_FORMAT(#{beginTime},'%Y-%m-%d') and `date` <= DATE_FORMAT(#{endTime},'%Y-%m-%d') ) AS date_list LEFT JOIN (select date_format(aa.create_time, '%Y-%m-%d') DAY, sum(case when end_time is null then 0 else 1 end) reply_num from (select wkm.create_time, min(case when wkm.origin = 5 and wkm.send_time >= wkp.session_start_time and wkm.send_time <= wkp.session_end_time then wkm.send_time end) as end_time from we_kf_pool wkp left join we_kf_msg wkm on wkp.corp_id = wkm.corp_id and wkp.open_kf_id = wkm.open_kf_id and wkp.external_userid = wkm.external_userid where 1=1
and wkp.open_kf_id in
#{openKfId}
and wkp.user_id in
#{userId}
and DATE_FORMAT(wkp.create_time,'%Y-%m-%d') >= DATE_FORMAT(#{beginTime},'%Y-%m-%d') and DATE_FORMAT(wkp.create_time,'%Y-%m-%d') <= DATE_FORMAT(#{endTime},'%Y-%m-%d') group by wkp.id, wkp.open_kf_id, wkp.external_userid) as aa group by DAY, (SELECT @reply_total := (select sum(case when end_time is null then 0 else 1 end) reply_num from (select wkm.create_time, min(case when wkm.origin = 5 and wkm.send_time >= wkp.session_start_time and wkm.send_time <= wkp.session_end_time then wkm.send_time end) as end_time from we_kf_pool wkp left join we_kf_msg wkm on wkp.corp_id = wkm.corp_id and wkp.open_kf_id = wkm.open_kf_id and wkp.external_userid = wkm.external_userid where 1=1
and wkp.open_kf_id in
#{openKfId}
and wkp.user_id in
#{userId}
and DATE_FORMAT(wkp.create_time, '%Y-%m-%d') <= DATE_FORMAT(#{endTime}, '%Y-%m-%d') group by wkp.id, wkp.open_kf_id, wkp.external_userid) as aa))) AS real_data ON date_list.`date` = real_data.day ORDER BY DAY ASC) AS RESULT ORDER BY x_time desc

SELECT date_list.`date` AS x_time, IFNULL(real_data.avg_consult_duration, 0) AS avg_consult_duration, IFNULL(real_data.avg_consult_reply_duration, 0) AS avg_consult_reply_duration from (select `date` from sys_dim_date where `date` >= DATE_FORMAT(#{beginTime},'%Y-%m-%d') and `date` <= DATE_FORMAT(#{endTime},'%Y-%m-%d')) AS date_list left join (select aa.day, aa.avg_consult_duration, AVG(timestampdiff(second, ifnull(aa.start_time, 0), ifnull(aa.end_time, 0))) as avg_consult_reply_duration from ( select date_format(wkp.create_time, '%Y-%m-%d') as day, AVG(timestampdiff(second, ifnull(wkp.session_start_time, 0), ifnull(wkp.session_end_time, 0))) as avg_consult_duration, min(case when wkm.send_time >= wkp.session_start_time then wkm.send_time end) start_time, min(case when wkm.origin = 5 and wkm.send_time >= wkp.session_start_time and wkm.send_time <= wkp.session_end_time then wkm.send_time end) as end_time from we_kf_pool wkp left join we_kf_msg wkm on wkp.open_kf_id = wkm.open_kf_id and wkp.external_userid = wkm.external_userid
and wkp.open_kf_id in
#{openKfId}
and wkp.user_id in
#{userId}
group by wkp.open_kf_id, wkp.external_userid ) as aa group by day) AS real_data ON date_list.`date` = real_data.day order by x_time desc
