mapper.select:
SELECT t2.follower_name as userName, count(t1.id) AS todayFollowNum FROM we_leads_follow_record t1 LEFT JOIN we_leads_follower t2 ON t1.follow_user_id = t2.id WHERE t1.record_status = 3
AND date_format(t1.create_time, '%Y-%m-%d') BETWEEN #{beginTime} AND #{endTime}
GROUP BY t2.follower_id ORDER BY todayFollowNum DESC LIMIT 5

SELECT t1.id, t1.sea_id, t1.we_leads_id, t1.record_status, t1.create_time, t1.follow_user_id FROM we_leads_follow_record t1 LEFT JOIN we_leads_follower t2 ON t1.follow_user_id = t2.id
AND t1.sea_id = #{seaId} AND date_format( t1.create_time, '%Y-%m-%d' ) = #{createTime}
AND t2.follower_we_user_id = #{weUserId} AND t1.record_status = #{recordStatus}
