mapper.select:
SELECT t3.dateTime ,(@total:= @total+ t3.todayReceiveNum) as allReceiveNum FROM (
) as t3, (select @total:= (SELECT count( 1 ) FROM we_leads_follower WHERE sea_id = 1
AND follower_we_user_id = #{weUserId}
AND follower_status = 1 AND DATE_FORMAT( follower_start_time, '%Y-%m-%d' ) < DATE_FORMAT(#{startTime},'%Y-%m-%d'))) as t4

mapper.sql:
SELECT t1.date as dateTime ,IFNULL(t2.today,0) as todayReceiveNum FROM sys_dim_date t1 LEFT JOIN ( SELECT DATE_FORMAT(follower_start_time, '%Y-%m-%d' ) as dateTime ,count(1) as today FROM we_leads_follower WHERE sea_id = #{seaId} AND follower_status = 1
AND follower_we_user_id = #{weUserId}
AND DATE_FORMAT( follower_start_time, '%Y-%m-%d' ) >= DATE_FORMAT( #{startTime}, '%Y-%m-%d' ) AND DATE_FORMAT( follower_start_time, '%Y-%m-%d' ) <= DATE_FORMAT( #{endTime}, '%Y-%m-%d' ) GROUP BY DATE_FORMAT(follower_start_time, '%Y-%m-%d' ) ) AS t2 ON t1.date = t2.dateTime WHERE date <= DATE_FORMAT(#{endTime}, '%Y-%m-%d' ) AND date >= DATE_FORMAT(#{startTime}, '%Y-%m-%d' ) ORDER BY date

mapper.select:
SELECT t6.dateTime,todayFollowNum,(@total:=@total+todayFollowNum) as allFollowerNum  FROM
(

) AS t6,
(select @total:= (SELECT count( 1 ) FROM we_leads_follow_record t1
LEFT JOIN we_leads_follower t2 on t1.follow_user_id = t2.id
WHERE t1.sea_id = #{seaId}
AND t1.record_status = 1

    AND t2.follower_we_user_id = #{weUserId}

AND DATE_FORMAT(t1.create_time, '%Y-%m-%d' ) < DATE_FORMAT(#{startTime},'%Y-%m-%d'))) as t7

mapper.sql:
SELECT t5.date as dateTime ,IFNULL(t4.sum,0) as todayFollowNum FROM sys_dim_date t5 left join ( select dateTime ,sum(count) as sum from ( select DATE_FORMAT( create_time, '%Y-%m-%d' ) as dateTime,count(1) AS count from we_leads_follow_record t1 left join we_leads_follower t2 on t1.follow_user_id = t2.id where t1.sea_id = #{seaId} AND t1.record_status = 1
AND t2.follower_we_user_id = #{weUserId}
AND DATE_FORMAT( t1.create_time, '%Y-%m-%d' ) >= DATE_FORMAT( #{startTime}, '%Y-%m-%d' ) AND DATE_FORMAT( t1.create_time, '%Y-%m-%d' ) <= DATE_FORMAT( #{endTime}, '%Y-%m-%d' ) group by t1.follow_user_id,DATE_FORMAT( t1.create_time, '%y-%m-%d' ) ) as t3 group by dateTime ) as t4 on t5.date = t4.dateTime WHERE t5.date >= DATE_FORMAT(#{startTime}, '%Y-%m-%d' ) AND t5.date <= DATE_FORMAT(#{endTime}, '%Y-%m-%d' ) ORDER BY t5.date
