mapper.select:
select a.* FROM ( SELECT wu.we_user_id as user_id, wu.user_name, wu.update_time as dimissionTime, (SELECT GROUP_CONCAT(wd.dept_name) FROM sys_dept wd WHERE wd.dept_id=wu.dept_id) as department, (SELECT COUNT(*) FROM we_allocate_customer wac WHERE wac.handover_userid=wu.we_user_id) as allocateCustomerNum, (SELECT COUNT(*) FROM we_allocate_group wag WHERE wag.old_owner=wu.we_user_id) as allocateGroupNum FROM sys_user wu
and wu.user_name like concat('%', #{userName}, '%')
AND date_format(wu.update_time,'%Y-%m-%d') >= date_format(#{beginTime},'%Y-%m-%d')
AND date_format(wu.update_time,'%Y-%m-%d') <= date_format(#{endTime},'%Y-%m-%d')
and wu.is_allocate = #{isAllocate}
) a WHERE a.allocateCustomerNum != 0 or a.allocateGroupNum !=0

SELECT wac.customer_name, wac.takeover_name as takeUserName, wac.allocate_time, wac.external_userid as externalUserid, wac.takeover_dept_name as deptNames, wac.takeover_dept_name as takeoverDeptName FROM we_allocate_customer wac
and wac.leave_user_id=#{leaveUserId}
and DATE_FORMAT(wac.update_time,'%Y-%m-%d') BETWEEN #{beginTime} AND #{endTime}

SELECT wag.takeover_name as newOwnerName, wag.chat_id, wag.chat_name as group_name, wag.takeover_dept_name as deptNames, wag.takeover_dept_name as department, wag.allocate_time FROM we_allocate_group wag
AND wag.leave_user_id=#{leaveUserId}
AND DATE_FORMAT(wag.update_time,'%Y-%m-%d') BETWEEN #{beginTime} AND #{endTime}
