mapper.select:
SELECT su.user_name, COUNT(wlt.id) as estimateSendNumber, COUNT(IF(wlt.send_state=1,wlt.id,NULL)) as actualSendNumber FROM we_live_tip wlt LEFT JOIN sys_user su ON wlt.send_we_userid = su.we_user_id WHERE wlt.del_flag=0
and wlt.send_target_type = #{sendTargetType}
and wlt.live_id=#{liveId}
and su.user_name like CONCAT('%',#{userName},'%')
GROUP BY su.we_user_id

SELECT
(SELECT GROUP_CONCAT(DISTINCT wg.group_name) FROM we_group wg WHERE wg.chat_id=wlt.send_target_id) as customerOrGroupName,
(SELECT GROUP_CONCAT(DISTINCT wc.customer_name) FROM we_customer wc WHERE wc.add_user_id=wlt.send_we_userid and wc.external_userid=wlt.send_target_id) as customerOrGroupName,
su.user_name AS userName, wlt.send_state, wlt.update_time FROM we_live_tip wlt LEFT JOIN sys_user su ON su.we_user_id=wlt.send_we_userid WHERE wlt.del_flag=0
and wlt.live_id=#{liveId}
and su.user_name like CONCAT('%',#{userName},'%')
AND wlt.send_state=#{sendState}
AND wlt.send_target_type=#{sendTargetType}

SELECT COUNT(DISTINCT send_we_userid) as estimateSendNumber, COUNT(DISTINCT if(send_state!=0,send_we_userid,NULL)) as actualSendNumber, COUNT(DISTINCT if(send_state=0,send_we_userid,NULL)) as noSendNumber FROM we_live_tip where del_flag=0
AND live_id=#{liveId}
AND send_target_type=#{sendTargetType}

SELECT SUM(aa.estimateSendNumber) as estimateSendNumber, SUM(aa.actualSendNumber) as actualSendNumber, SUM(aa.noSendNumber) as noSendNumber FROM (
SELECT COUNT(send_target_id) as estimateSendNumber, COUNT(if(send_state!=0,send_target_id,NULL)) as actualSendNumber, COUNT(if(send_state=0,send_target_id,NULL)) as noSendNumber FROM we_live_tip where del_flag=0
AND live_id=#{liveId}
AND send_target_type=#{sendTargetType}
)aa
