mapper.select:
SELECT wm.id,wm.category_id,wm.material_url,wm.content,wm.material_name,wm.digest,wm.cover_url,wm.create_by,wm.create_time,wm.update_by,wm.update_time,wm.audio_time,wm.link_url FROM we_material wm LEFT JOIN we_category wc ON wm.category_id=wc.id
wm.del_flag=0
AND wm.category_id=#{categoryId}
AND (wm.material_name LIKE CONCAT('%',#{search},'%') OR wm.content LIKE CONCAT('%',#{search},'%'))
AND wc.media_type=#{mediaType}

select
t.*,
(SELECT GROUP_CONCAT(wt.name) from we_tag wt WHERE  FIND_IN_SET(wt.tag_id,t.tag_ids)) as tagNames,
tt.name as categoryName,
(
SELECT count( 1 ) FROM we_content_send_record t1

    t.id = t1.content_id
    and t1.resource_type = #{resourceType}

) AS sendNum,
(
SELECT count( 1 ) FROM we_content_view_record t1

    t.id = t1.content_id
    and t1.resource_type = #{resourceType}

) AS viewNum,
(
SELECT count( DISTINCT view_openid ) FROM we_content_view_record t1

    t.id = t1.content_id
    and t1.resource_type = #{resourceType}

) AS viewByNum
from
we_material t
left join we_category tt on t.category_id = tt.id

    t.del_flag = 0
    and t.id = #{materialId}
    and category_id = #{categoryId}
    and material_name like
        concat('%',#{materialName},'%')

    and t.media_type = #{mediaType}
    and t.type = #{type}

        and t.media_type not in

            #{mediaType}



        and t.module_type in

            #{moduleType}


    and t.pixel_size <= #{pixelSize}
    and t.memory_size <= #{memorySize}

order by t.update_time desc

SELECT
t.*,
(
SELECT count( 1 ) FROM we_content_send_record t1

    t.id = t1.content_id
    and t1.resource_type = #{resourceType}

) AS send_total_num,
(
SELECT count( 1 ) FROM we_content_view_record t1

    t.id = t1.content_id
    and t1.resource_type = #{resourceType}

) AS view_total_num,
(
SELECT count( DISTINCT view_openid ) FROM we_content_view_record t1

    t.id = t1.content_id
    and t1.resource_type = #{resourceType}

) AS view_by_total_num
FROM
we_material t
WHERE
t.del_flag = 0
and module_type = #{moduleType}

SELECT t.id AS talkId, t.talk_title, t2.*, ( SELECT count( 1 ) FROM we_content_send_record ft
t2.id = ft.content_id AND ft.talk_id = t.id and ft.resource_type = #{resourceType} and DATE_FORMAT(ft.send_time,'%Y-%m-%d') >= DATE_FORMAT(#{beginTime},'%Y-%m-%d')
and DATE_FORMAT(ft.send_time,'%Y-%m-%d') <= DATE_FORMAT(#{endTime},'%Y-%m-%d')
) AS send_total_num, ( SELECT count( 1 ) from we_content_view_record ft
t2.id = ft.content_id AND ft.talk_id = t.id and ft.resource_type = #{resourceType} and DATE_FORMAT(ft.view_time,'%Y-%m-%d') >= DATE_FORMAT(#{beginTime},'%Y-%m-%d')
and DATE_FORMAT(ft.view_time,'%Y-%m-%d') <= DATE_FORMAT(#{endTime},'%Y-%m-%d')
) AS view_total_num, ( SELECT count(DISTINCT view_openid ) FROM we_content_view_record ft
t2.id = ft.content_id and ft.resource_type = #{resourceType} and DATE_FORMAT(ft.view_time,'%Y-%m-%d') >= DATE_FORMAT(#{beginTime},'%Y-%m-%d')
and DATE_FORMAT(ft.view_time,'%Y-%m-%d') <= DATE_FORMAT(#{endTime},'%Y-%m-%d')
) AS view_by_total_num FROM we_content_talk t INNER JOIN we_talk_material t1 ON t.id = t1.talk_id INNER JOIN we_material t2 ON t2.id = t1.material_id
t.del_flag = 0 and t2.del_flag = 0 and t1.talk_id = #{talkId} and t.talk_type = #{talkType}
order by t1.sort

select t.* from we_material t inner join we_tlp_material t1 on t.id = t1.material_id
t.del_flag = 0
AND t1.tlp_id = #{tlpId}
