mapper.select:
SELECT t1.id, t1.name, t1.type, t1.send_type, t1.is_lw_push, t1.scope_type, t1.customer_num, t1.dept_ids, t1.post_ids, t1.user_ids, t1.customer_tag, t1.content, t1.execute_time, t1.execute_end_time, t1.like_tag_ids, t1.comment_tag_ids, t1.status, t1.create_by, t1.create_by_id, t1.create_time, t1.update_by, t1.update_by_id, t1.update_time, t1.del_flag, CASE t1.send_type WHEN 0 THEN ( SELECT count( 1 ) FROM we_moments_user t2 WHERE t2.moments_task_id = t1.id AND t2.execute_status = 1 ) WHEN 2 THEN (SELECT count( 1 ) FROM we_moments_estimate_user t2 WHERE t2.moments_task_id = t1.id AND t2.execute_status = 1) END AS executed, CASE t1.send_type WHEN 0 THEN (SELECT count( 1 ) FROM we_moments_user t2 WHERE t2.moments_task_id = t1.id) WHEN 2 THEN (SELECT count( 1 ) FROM we_moments_estimate_user t2 WHERE t2.moments_task_id = t1.id ) END AS total FROM we_moments_task t1 WHERE t1.del_flag = 0 AND t1.send_type !=1
AND t1.send_type = #{sendType}
AND t1.status = #{status}
AND t1.execute_time BETWEEN #{executeStartTime} AND #{executeEndTime}
AND t1.name like CONCAT('%',#{name},'%')
AND t1.create_by like CONCAT('%',#{createBy},'%')
ORDER BY t1.update_time DESC
