mapper.select:
SELECT
t.*,
( SELECT count( 1 ) FROM we_tlp_material t1 WHERE t.id = t1.tlp_id) AS attachTotalNum
FROM
we_msg_tlp t

    t.del_flag = 0
    and t.tpl_type = #{query.tplType}
    and t.category_id = #{query.categoryId}
    and t.template_type = #{query.templateType}
    and t.template_info like concat('%',
        #{query.templateInfo},'%')



            #{item}



order by update_time desc
