mapper.select:
select wqrmn.user_id, wqrm.chat_type, wqrm.room_id, wqrm.from_id, wqrm.send_time, wqrm.reply_status, ifnull(wqrm.reply_time,now()) as reply_time, if(wqrm.chat_type = 1,wc.customer_name,wg.group_name) as chat_name, wqrmn.create_time, wqrm.msg_id from we_qi_rule_msg_notice wqrmn inner join we_qi_rule_msg wqrm on wqrm.id = wqrmn.qi_rule_msg_id left join we_group wg on wqrm.room_id = wg.chat_id and (case when wqrm.chat_type = 2 then 1 else 0 end ) = 1 and wg.del_flag = 0 left join we_customer wc on wqrm.from_id = wc.external_userid and wqrm.receive_id = wc.add_user_id and wc.del_flag = 0 and (case when wqrm.chat_type = 1 then 1 else 0 end ) = 1 where wqrmn.type = 1 and wqrm.rule_id = #{ruleId}
and date_format(wqrmn.create_time,'%y-%m-%d') >= date_format(#{beginTime},'%y-%m-%d')
and date_format(wqrmn.create_time,'%y-%m-%d') <= date_format(#{endTime},'%y-%m-%d')
and wqrmn.user_id in
#{item}
order by wqrmn.create_time desc
