mapper.select:
select wqrmn.id, wqrmn.qi_rule_msg_id, wqrmn.user_id, wqrmn.type, wqrmn.status, wqrmn.msg_id, wqrmn.create_time, wqrm.rule_id, wqrm.msg_id as chat_msg_id, wqrm.from_id, wqrm.receive_id, wqrm.room_id, wg.group_name as room_name, wqrm.chat_type, wqrm.send_time, wqrm.reply_time, wqrm.reply_msg_id, wqrm.reply_status, TIMESTAMPDIFF(MINUTE, wqrm.send_time, ifnull(wqrm.reply_time,now())) as time_minutes, wqr.name as rule_name, wccm.action, wccm.msg_type, wccm.seq, wccm.contact from we_qi_rule_msg_notice wqrmn inner join we_qi_rule_msg wqrm on wqrmn.qi_rule_msg_id = wqrm.id left join we_qi_rule wqr on wqrm.rule_id = wqr.id left join we_chat_contact_msg wccm on wqrm.msg_id = wccm.msg_id left join we_group wg on wqrm.room_id = wg.chat_id and wqrm.chat_type = 2
and wqrmn.type = #{type}
and wqr.name like concat('%', #{name}, '%')
and wqr.id = #{ruleId}
and wqrmn.user_id in
#{item}
and date_format(wqrmn.create_time,'%y-%m-%d') >= date_format(#{beginTime},'%y-%m-%d')
and date_format(wqrmn.create_time,'%y-%m-%d') <= date_format(#{endTime},'%y-%m-%d')
order by time_minutes desc
