mapper.select:
SELECT DISTINCT wre.open_id, (SELECT wu.user_name FROM sys_user wu WHERE wu.we_user_id=wre.we_user_id) as userName, wre.receive_name as customerName, wre.avatar, IFNULL(Round(wre.red_envelope_money/100,2),0.00) as redEnvelopeMoney, wre.create_time, wre.send_state, wre.order_no FROM we_red_envelopes_record wre WHERE wre.receive_type=1 and wre.chat_id IS NULL
AND wre.we_user_id in
#{item}
AND wre.send_state=#{sendState}
AND DATE_FORMAT(wre.create_time,'%Y-%m-%d') BETWEEN #{beginTime} AND #{endTime}
AND wre.receive_name like concat('%',#{customerName},'%')
ORDER BY wre.create_time DESC

SELECT (SELECT wu.user_name FROM sys_user wu WHERE wu.we_user_id=wre.we_user_id) as userName, wg.group_name as groupName, wre.red_envelope_type, wre.red_envelope_num, IFNULL(Round(wre.red_envelope_money/100,2),0.00) as redEnvelopeMoney, wre.create_time, wre.chat_id, wre.order_no, wu.user_name as groupLeaderName, (select count(*) from we_red_envelopes_record wred where wred.chat_id=wre.chat_id and wred.source=2 and wre.order_no=wred.order_no and wred.open_id is not null ) as receiveNum, (wre.red_envelope_num-(select count(*) from we_red_envelopes_record wred where wred.chat_id=wre.chat_id and wred.source=2 and wre.order_no=wred.order_no and wred.open_id is not null )) as surplusNum FROM we_red_envelopes_record wre LEFT JOIN we_group wg ON wg.chat_id=wre.chat_id LEFT JOIN sys_user wu on wu.we_user_id=wg.owner WHERE wre.receive_type=2 and wre.source=1
AND wre.we_user_id in
#{item}
AND wg.group_name like concat('%',#{groupName},'%')
AND wre.red_envelope_type=#{redEnvelopeType}
AND DATE_FORMAT(wre.create_time,'%Y-%m-%d') BETWEEN #{beginTime} AND #{endTime}
ORDER BY wre.create_time DESC

SELECT DISTINCT wre.open_id, wre.receive_name as customerName, IFNULL(Round(wre.red_envelope_money/100,2),0.00) as redEnvelopeMoney, wre.create_time, wre.receive_order_no as orderNo FROM we_red_envelopes_record wre where wre.open_id is not null and wre.source=2 and wre.send_state=2
AND wre.chat_id=#{chatId}
and wre.order_no=#{orderNo}

SELECT wr.* FROM ( SELECT IFNULL(COUNT(0),0) as totalNum, IFNULL(SUM(wre.red_envelope_money),0) as totalMoney, DATE_FORMAT(wre.create_time,'%Y-%m-%d') as createTime FROM we_red_envelopes_record wre WHERE wre.send_state=2 and DATE_FORMAT(wre.create_time,'%Y-%m-%d') between #{startTime} and #{endTime} GROUP BY DATE_FORMAT(wre.create_time,'%Y-%m-%d')
UNION(SELECT 0,0,#{date})
) AS wr GROUP BY wr.createTime ORDER BY wr.createTime ASC

SELECT (SELECT wu.user_name FROM sys_user wu WHERE wu.we_user_id=wre.we_user_id) as userName, wre.receive_type, wre.create_time, SUM(wre.red_envelope_money) as totalMoney FROM we_red_envelopes_record wre WHERE wre.send_state=2
and DATE_FORMAT(wre.create_time,'%Y-%m-%d') between #{startTime} and #{endTime}
GROUP BY wre.we_user_id,wre.receive_type

SELECT IFNULL(sum(wrer.red_envelope_money),0) AS accpectMoney FROM we_red_envelopes_record wrer WHERE wrer.open_id IS NOT NULL
and wrer.order_no=#{orderNo}
and wrer.send_state=2
