SELECT wsb.send_type, wset.execute_we_user_id, wset.target_id, wset.target_type, wseta.sop_attachment_id, wsb.id as sop_base_id, wseta.push_start_time, wseta.push_end_time, wseta.id as excuteTargetAttachId FROM we_sop_execute_target_attachments wseta LEFT JOIN we_sop_execute_target wset ON wset.id = wseta.execute_target_id LEFT JOIN we_sop_base wsb ON wset.sop_base_id = wsb.id WHERE wsb.del_flag=0 and wsb.sop_state=1 and wset.execute_state=1
AND wset.target_type=#{targetType}
AND wsb.send_type=#{sendType}
AND timestampdiff(MINUTE,NOW(),wseta.push_end_time) BETWEEN 0 AND 10 AND wseta.is_tip !=2
AND to_days(wseta.push_start_time) = to_days(now()) AND wseta.is_tip=0

SELECT wsb.send_type, wset.execute_we_user_id, wset.target_id, wset.target_type, wseta.sop_attachment_id, wsb.id as sop_base_id, wseta.push_start_time, wseta.push_end_time, wseta.id as excuteTargetAttachId FROM we_sop_execute_target_attachments wseta LEFT JOIN we_sop_execute_target wset ON wset.id = wseta.execute_target_id LEFT JOIN we_sop_base wsb ON wset.sop_base_id = wsb.id WHERE wsb.del_flag=0 and wsb.sop_state=1 and wset.execute_state=1 AND to_days(wseta.push_start_time) = to_days(now()) AND wseta.is_tip=0
AND wset.target_type = #{targetType}
AND wsb.send_type = #{sendType}
AND wset.execute_we_user_id = #{weUserId}
