mapper.select:
SELECT
    (
        SELECT
            COUNT(DISTINCT unionid)
        from
            we_store_code_count where source=1
        and store_code_id=#{storeCodeId}
    )  as totalShopGuideScannNumber,
    (
        SELECT
            COUNT(DISTINCT if(date_format(create_time,'%y%m%d') = date_format(curdate(),'%y%m%d'),unionid,0))
        from
            we_store_code_count where source=1
        and store_code_id=#{storeCodeId}
    ) as tdShopGuideScannNumber,

    (
        SELECT count(DISTINCT external_userid) from we_customer where state in (
            SELECT state from we_store_code

                id=#{storeCodeId}

        )
    ) as customerTotalNumber,
    (
        SELECT count(DISTINCT external_userid) from we_customer where state in (
            SELECT state from we_store_code

            id=#{storeCodeId}

        ) AND  date_format(add_time,'%y%m%d') = date_format(curdate(),'%y%m%d')
    ) as ydCustomerNumber,

    (
        SELECT
            COUNT(DISTINCT unionid)
        from
            we_store_code_count where source=2
        and store_code_id=#{storeCodeId}
)  as totalStoreGroupScannNumber,
    (
        SELECT
            COUNT(DISTINCT if(date_format(create_time,'%y%m%d') = date_format(curdate(),'%y%m%d'),unionid,0))
        from
            we_store_code_count where source=2
       and store_code_id=#{storeCodeId}
) as tdStoreGroupScannNumber,

    (
        SELECT COUNT(DISTINCT wgm.user_id)
        FROM we_group_member wgm
        where wgm.state in (
            SELECT state from we_group_code

                id=#{groupCodeId}

        )
        AND wgm.del_flag=0

    ) as totalJoinGroupMemberNumber,

    (
        SELECT COUNT(DISTINCT wgm.user_id)
        FROM we_group_member wgm
        where wgm.state in (
            SELECT state from we_group_code

                id=#{groupCodeId}

    ) and date_format(join_time,'%y%m%d') = date_format(curdate(),'%y%m%d')
    ) as tdJoinGroupMemberNumber,

    (
        SELECT COUNT(DISTINCT wgm.user_id)
        FROM we_group_member wgm
        where wgm.state in (
        SELECT state from we_group_code

                id=#{groupCodeId}

    ) 	AND wgm.del_flag=1

    ) as totalExitGroupMemberNumber,

    (
        SELECT COUNT(DISTINCT wgm.user_id)
        FROM we_group_member wgm
        where wgm.state in (
            SELECT state from we_group_code

                id=#{groupCodeId}

        )  	AND wgm.del_flag=1 AND  date_format(quit_time,'%y%m%d') = date_format(curdate(),'%y%m%d')
    ) as tdExitGroupMemberNumber

SELECT
    wscc.store_code_id,
    date_format(wsc.create_time,'%Y-%m-%d') as createTime,
    wsc.address,
    wsc.area,
    wsc.store_name,
    COUNT(IF(date_format( wscc.create_time, '%Y-%m-%d' ) BETWEEN #{weStoreCode.beginTime} AND #{weStoreCode.endTime},unionid,0)) as totalStoreGroupScannNumber,
    (SELECT COUNT(DISTINCT wgm.user_id) FROM we_group_member wgm WHERE wgm.state=wsc.state and wgm.quit_scene IS NULL
                                                                   AND date_format(wgm.create_time, '%Y-%m-%d' ) BETWEEN #{weStoreCode.beginTime} AND #{weStoreCode.endTime}
    ) as totalJoinGroupMemberNumber,

    (SELECT COUNT(DISTINCT wgm.user_id) FROM we_group_member wgm WHERE wgm.state=wsc.state and wgm.quit_scene IS NOT NULL
                                                                   AND date_format(wgm.create_time, '%Y-%m-%d' ) BETWEEN #{weStoreCode.beginTime} AND #{weStoreCode.endTime}
    ) as totalExitGroupMemberNumber,

    COUNT(DISTINCT if(date_format(wscc.create_time,'%y%m%d') = date_format(curdate(),'%y%m%d'),unionid,0)) as tdStoreGroupScannNumber,

    (SELECT COUNT(DISTINCT wgm.user_id) FROM we_group_member wgm WHERE wgm.state=wsc.state  and wgm.quit_scene IS NULL
                                                                   AND date_format(wgm.create_time, '%Y-%m-%d' )  = date_format(curdate(),'%Y-%m-%d')
    ) as tdJoinGroupMemberNumber,

    (SELECT COUNT(DISTINCT wgm.user_id) FROM we_group_member wgm WHERE wgm.state=wsc.state  and wgm.quit_scene IS NOT NULL
                                                                   AND date_format(wgm.create_time, '%Y-%m-%d' )  = date_format(curdate(),'%Y-%m-%d')
    ) as tdExitGroupMemberNumber

FROM
    we_store_code_count wscc
        LEFT JOIN we_store_code wsc ON wscc.store_code_id=wsc.id
WHERE wscc.source=2 and wsc.del_flag=0
and wsc.area = #{weStoreCode.area}
and wsc.store_name = #{weStoreCode.storeName}
 and wsc.id=#{weStoreCode.storeCodeId}
GROUP BY date_format(wsc.create_time,'%Y-%m-%d')

SELECT a.date as dataTime,IFNULL(b.totalExitGroupMemberNumber,0) as totalExitGroupMemberNumber ,IFNULL(c.totalJoinGroupMemberNumber,0) as totalJoinGroupMemberNumber, IFNULL(d.totalStoreGroupScannNumber,0) as totalStoreGroupScannNumber FROM
( SELECT date FROM sys_dim_date WHERE date_format(date,'%Y-%m-%d') BETWEEN #{weStoreCode.beginTime} AND #{weStoreCode.endTime} GROUP BY date ) a LEFT JOIN (
SELECT wgm.quit_time, count( DISTINCT wgm.user_id ) as totalExitGroupMemberNumber FROM we_group_member wgm WHERE wgm.state IN ( SELECT wgc.state FROM we_store_code wsc LEFT JOIN we_group_code wgc ON wgc.id = wsc.group_code_id
wsc.id=#{weStoreCode.storeCodeId}
) and date_format(wgm.quit_time,'%Y-%m-%d') BETWEEN #{weStoreCode.beginTime} AND #{weStoreCode.endTime} AND wgm.del_flag=1 GROUP BY date_format(wgm.quit_time,'%Y-%m-%d') ORDER BY wgm.quit_time desc ) b ON a.date=date_format(b.quit_time,'%Y-%m-%d')
LEFT JOIN (
SELECT wgm.join_time, count( DISTINCT wgm.user_id ) as totalJoinGroupMemberNumber FROM we_group_member wgm WHERE wgm.state IN ( SELECT wgc.state FROM we_store_code wsc LEFT JOIN we_group_code wgc ON wgc.id = wsc.group_code_id
wsc.id=#{weStoreCode.storeCodeId}
) and date_format(wgm.join_time,'%Y-%m-%d') BETWEEN #{weStoreCode.beginTime} AND #{weStoreCode.endTime} GROUP BY date_format(wgm.join_time,'%Y-%m-%d') ORDER BY wgm.join_time desc ) c ON a.date=date_format(c.join_time,'%Y-%m-%d')
LEFT JOIN (
SELECT create_time,count(DISTINCT unionid) as totalStoreGroupScannNumber from we_store_code_count where source=2 and date_format(create_time,'%Y-%m-%d') BETWEEN #{weStoreCode.beginTime} AND #{weStoreCode.endTime}
AND store_code_id=#{weStoreCode.storeCodeId}
GROUP BY date_format(create_time,'%Y-%m-%d') ORDER BY create_time desc
) d ON a.date=date_format(d.create_time,'%Y-%m-%d') ORDER BY a.date asc

SELECT wc.avatar, wc.customer_name, wc.customer_type, wc.external_userid AS externalUserid, wc.add_user_id, wc.add_time, wc.gender, (SELECT GROUP_CONCAT(su.user_name) FROM sys_user su WHERE su.we_user_id=wc.add_user_id) as addUserName, (SELECT count(*) from we_group_member wgm WHERE wc.external_userid = wgm.user_id and wgm.state=#{query.groupCodeState} and wgm.quit_scene IS NULL) as joinGroupNumber, IF((SELECT count(*) from we_group_member wgm WHERE wc.external_userid = wgm.user_id and wgm.state=#{query.groupCodeState} and wgm.quit_scene IS NULL)>0,1,0) as isJoinGroup FROM we_customer wc
and wc.state = #{query.shopGuideState}
and wc.customer_name LIKE CONCAT('%',#{query.customerName,jdbcType=VARCHAR},'%')
AND date_format(wcr.add_time,'%Y-%m-%d') BETWEEN #{query.startAddTime} AND #{query.endAddTime}
HAVING(joinGroupNumber)>0
