mapper.insert:
INSERT INTO we_tag_group (id,group_id,group_name,create_by,create_time,update_by,update_time,create_by_id,update_by_id) VALUES
(#{item.id},#{item.groupId},#{item.groupName},#{item.createBy},#{item.createTime},#{item.updateBy},#{item.updateTime},#{item.createById},#{item.updateById})
ON DUPLICATE KEY UPDATE group_name = IFNULL(VALUES(group_name), we_tag_group.group_name), update_by = IFNULL(VALUES(update_by), we_tag_group.update_by), update_by_id = IFNULL(VALUES(update_by_id), we_tag_group.update_by_id), update_time = IFNULL(VALUES(update_time), we_tag_group.update_time), del_flag = IFNULL(VALUES(del_flag), we_tag_group.del_flag);

mapper.select:
select distinct wtg.id from we_tag_group wtg left join we_tag wt on wtg.group_id = wt.group_id and wt.del_flag = 0
and wtg.group_tag_type=#{groupTagType}
and wtg.del_flag = 0 order by wtg.create_time desc

SELECT wtg.id, wtg.group_id, wtg.group_name, wtg.group_tag_type, wtg.owner, wt.id as t_id, wt.tag_id, wt.name as tag_name, wt.tag_type FROM we_tag_group wtg left join we_tag wt on wtg.group_id = wt.group_id and wt.del_flag = 0
and wtg.group_id = #{groupId}
and wtg.group_tag_type=#{groupTagType}
and wtg.del_flag = 0 order by wtg.create_time desc
