mapper.select:
select wgmt.id, wgmt.msg_template_id, wgmt.msg_id, wgmt.user_id, (select wu.user_name from we_user wu where wu.user_id = wgmt.user_id limit 1) as user_name, wgmt.status, wgmt.send_time, (select count(1) from we_group_message_send_result wgmsr where wgmsr.user_id = wgmt.user_id and wgmsr.msg_template_id = wgmt.msg_template_id and wgmsr.status = 0) as to_be_customer_name, (select count(1) from we_group_message_send_result wgmsr where wgmsr.user_id = wgmt.user_id and wgmsr.msg_template_id = wgmt.msg_template_id and wgmsr.status = 1) as already_customer_name from we_group_message_task wgmt
and wgmt.id = #{id}
and wgmt.msg_id = #{msgId}
and wgmt.msg_template_id = #{msgTemplateId}
and exists (select 1 from we_user wu where wu.user_id = wgmt.user_id and wu.user_name like concat('%', #{userName}, '%'))
and wgmt.status = #{status}
AND date_format(wgmt.send_time,'%y%m%d') >= date_format(#{beginTime},'%y%m%d')
AND date_format(wgmt.send_time,'%y%m%d') <= date_format(#{endTime},'%y%m%d')
